$(function(){$.fn.ircti=function(a){if((this.attr("type")=="radio"||this.attr("type")=="checkbox")&&$('input[name="'+this.attr("name")+'"]').length>0){var c="../img/checkbox_check_1.png";var d="../img/checkbox_no_check_1.png";if(this.attr("type")=="radio"){c="../img/radiobox_check_1.png";d="../img/radiobox_no_check_1.png"}var b=$.extend({height:"auto",width:"auto",url_img_check:c,url_img_no_check:d},a);$.each($('input[name="'+this.attr("name")+'"]'),function(){var e=$(this);e.hide();if(e.is(":checked")){e.after('<span data-ircti_lk_id="'+e.attr("id")+'" data-ircti_lk_name="'+e.attr("name")+'"> <img src="'+b.url_img_check+'" alt="Input checked" class="ircti_img_input" style="height: '+b.height+"; width: "+b.width+'; cursor: pointer;"/> </span>')}else{e.after('<span data-ircti_lk_id="'+e.attr("id")+'" data-ircti_lk_name="'+e.attr("name")+'"> <img src="'+b.url_img_no_check+'" alt="Input no checked" class="ircti_img_input" style="height: '+b.height+"; width: "+b.width+'; cursor: pointer;"/> </span>')}});$('input[name="'+this.attr("name")+'"]').unbind("change").change(function(){var e=$(this);if(e.attr("type")=="radio"){$('span[data-ircti_lk_name="'+e.attr("name")+'"]').html('<img src="'+b.url_img_no_check+'" alt="Input no checked" style="height: '+b.height+"; width: "+b.width+'; cursor: pointer;"/>');$('span[data-ircti_lk_id="'+$('input[name="'+e.attr("name")+'"]:checked').attr("id")+'"]').html('<img src="'+b.url_img_check+'" alt="Input checked" style="height: '+b.height+"; width: "+b.width+'; cursor: pointer;"/>')}else{if($(this).prop("checked")){$('span[data-ircti_lk_id="'+e.attr("id")+'"]').html('<img src="'+b.url_img_check+'" alt="Input checked" style="height: '+b.height+"; width: "+b.width+'; cursor: pointer;"/>')}else{$('span[data-ircti_lk_id="'+e.attr("id")+'"]').html('<img src="'+b.url_img_no_check+'" alt="Input no checked" style="height: '+b.height+"; width: "+b.width+'; cursor: pointer;"/>')}}})}else{console.warn('There is no input "radio" or "checkbox" in the jquery object passed')}return this}});